import React from "react";

const getPriorityColor = (priority) => {
    switch (Number(priority)) {
        case 1: return "#e74c3c"; // Red - Highest
        case 2: return "#f39c12"; // Orange
        case 3: return "#f1c40f"; // Yellow
        case 4: return "#2ecc71"; // Green
        case 5: return "#3498db"; // Blue - Lowest
        default: return "#95a5a6"; // Gray
    }
};

const TaskItem = ({ task, onToggle, onDelete }) => (
    <div style={{
        display: "flex",
        alignItems: "center",
        padding: "12px 16px",
        borderBottom: "1px solid #eee",
        gap: "12px",
        transition: "background-color 0.2s ease",
        backgroundColor: task.done ? "#f9f9f9" : "transparent"
    }}>
        <div style={{
            width: "12px",
            height: "12px",
            borderRadius: "50%",
            backgroundColor: getPriorityColor(task.priority),
            flexShrink: 0
        }} title={`Priority: ${task.priority}`}></div>
        <span style={{
            flex: 1,
            textDecoration: task.done ? "line-through" : "none",
            color: task.done ? "#aaa" : "#333",
            fontSize: "1rem"
        }}>
            {task.title}
        </span>
        <div style={{ display: "flex", gap: "8px" }}>
            <button 
                onClick={() => onToggle(task.id)}
                style={{
                    background: "none",
                    border: "1px solid",
                    borderColor: task.done ? "#27ae60" : "#ccc",
                    color: task.done ? "#27ae60" : "#555",
                    borderRadius: 4,
                    padding: "4px 8px",
                    cursor: "pointer",
                    fontSize: "0.8em"
                }}
            >
                {task.done ? "✓ Done" : "Mark Done"}
            </button>
            <button 
                onClick={() => onDelete(task.id)}
                style={{
                    background: "none",
                    border: "none",
                    color: "#e74c3c",
                    cursor: "pointer",
                    fontSize: "1.2em",
                    fontWeight: "bold"
                }}
                title="Delete task"
            >
                ×
            </button>
        </div>
    </div>
);

export default function ToDoList({ tasks, onToggle, onDelete, search }) {
    const filteredTasks = tasks.filter(task => 
        task.title.toLowerCase().includes(search.toLowerCase())
    );

    if (tasks.length === 0) {
        return <p style={{ textAlign: "center", color: "#888", marginTop: "2rem" }}>No tasks yet. Add one above!</p>;
    }

    if (filteredTasks.length === 0) {
        return <p style={{ textAlign: "center", color: "#888", marginTop: "2rem" }}>No tasks found for "{search}".</p>;
    }

    return (
        <div style={{
            border: "1px solid #eee",
            borderRadius: 8,
            overflow: "hidden",
            marginTop: "1rem"
        }}>
            {filteredTasks.map(task => (
                <TaskItem 
                    key={task.id} 
                    task={task} 
                    onToggle={onToggle} 
                    onDelete={onDelete} 
                />
            ))}
        </div>
    );
}
