import React from "react";

export default function Controls({ onUndo, onRedo, canUndo, canRedo, search, setSearch }) {
    return (
        <div style={{
            display: "flex",
            alignItems: "center",
            marginBottom: "1.5rem",
            gap: "12px",
            flexWrap: "wrap"
        }}>
            <div style={{ display: "flex", gap: "8px" }}>
                <button 
                    onClick={onUndo} 
                    disabled={!canUndo} 
                    style={{
                        padding: "8px 16px",
                        border: "1px solid #ddd",
                        borderRadius: 4,
                        backgroundColor: canUndo ? "#3498db" : "#bdc3c7",
                        color: "white",
                        cursor: canUndo ? "pointer" : "not-allowed",
                        fontSize: "0.9em",
                        transition: "all 0.2s ease"
                    }}
                    onMouseOver={(e) => {
                        if (canUndo) e.target.style.backgroundColor = "#2980b9";
                    }}
                    onMouseOut={(e) => {
                        if (canUndo) e.target.style.backgroundColor = "#3498db";
                    }}
                >
                    â†¶ Undo
                </button>
                
                <button 
                    onClick={onRedo} 
                    disabled={!canRedo} 
                    style={{
                        padding: "8px 16px",
                        border: "1px solid #ddd",
                        borderRadius: 4,
                        backgroundColor: canRedo ? "#2ecc71" : "#bdc3c7",
                        color: "white",
                        cursor: canRedo ? "pointer" : "not-allowed",
                        fontSize: "0.9em",
                        transition: "all 0.2s ease"
                    }}
                    onMouseOver={(e) => {
                        if (canRedo) e.target.style.backgroundColor = "#27ae60";
                    }}
                    onMouseOut={(e) => {
                        if (canRedo) e.target.style.backgroundColor = "#2ecc71";
                    }}
                >
                    â†· Redo
                </button>
            </div>
            
            <div style={{ 
                flex: 1, 
                minWidth: "200px",
                position: "relative"
            }}>
                <input
                    type="text"
                    value={search}
                    placeholder="ðŸ” Search tasks..."
                    onChange={e => setSearch(e.target.value)}
                    style={{
                        width: "100%",
                        padding: "8px 12px",
                        border: "1px solid #ddd",
                        borderRadius: 4,
                        fontSize: "0.9em",
                        outline: "none",
                        transition: "border-color 0.2s ease"
                    }}
                    onFocus={(e) => e.target.style.borderColor = "#3498db"}
                    onBlur={(e) => e.target.style.borderColor = "#ddd"}
                />
                {search && (
                    <button
                        onClick={() => setSearch("")}
                        style={{
                            position: "absolute",
                            right: "8px",
                            top: "50%",
                            transform: "translateY(-50%)",
                            background: "none",
                            border: "none",
                            cursor: "pointer",
                            fontSize: "1.2em",
                            color: "#999",
                            padding: "0",
                            width: "20px",
                            height: "20px",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center"
                        }}
                        title="Clear search"
                    >
                        Ã—
                    </button>
                )}
            </div>
        </div>
    );
}
