import React from "react";

export default function Controls({ onUndo, onRedo, canUndo, canRedo, search, setSearch }) {
    return (
        <div style={{
            display: "flex",
            alignItems: "center",
            marginBottom: "1rem"
        }}>
            <button onClick={onUndo} disabled={!canUndo} style={{marginRight: 8}}>Undo</button>
            <button onClick={onRedo} disabled={!canRedo} style={{marginRight: 16}}>Redo</button>
            <input
                type="text"
                value={search}
                placeholder="Search tasks..."
                onChange={e => setSearch(e.target.value)}
                style={{flex: 1, padding: 4}}
                />
        </div>
    );
}
