import React from "react";

export default function ToDoList({ tasks, onToggle, onDelete, search}) {
    let filtered = tasks;
    if (search) {
        const q = search.trim().toLowerCase();
        filtered = tasks.filter(task => task.title.toLowerCase().includes(q));
    }
    return (
        <ul style={{paddingLeft: 0}}>
            {filtered.length === 0 &&
                <li style={{listStyle: "none", color: "#888"}}>No tasks found.</li>
                }
                {filtered.map(task => (
                    <li key={task.id} style={{
                        listStyle: "none",
                        marginBottom: 5,
                        padding: "8px 0",
                        borderBottom: "1px solid #eee",
                        display: "flex",
                        alignItems: "center"
                    }}>
                        <input
                            type="checkbox"
                            checked={task.done}
                            onChange={ () => onToggle(task.id)}
                            style={{marginRight: 8}}
                        />
                        <span style={{
                            textDecoration: task.done ? "line-through" : "none",
                            flex: 1
                        }}>
                            {task.title}{" "}
                            <span style={{
                            fontSize: "0.8em",
                            color: "#888"
                            }}>
                                (Priority: {task.priority})
                            </span>
                        </span>
                        <button onClick={() => onDelete(task.id)} style={{
                            background: "#e74c3c",
                            border: "none",
                            color: "white",
                            padding: "2px 8px",
                            borderRadius: 4,
                            cursor: "pointer"
                        }}>Delete</button>
                    </li>
                    ))}
                </ul>
        );
    }
